import React from "react";

const MovieDetails = (props) => {
  return (
    <div className="container">
      <div
        className="row"
        onClick={props.closedetails}
        style={{ cursor: "pointer", paddingTop: 50 }}
      >
        <i className="fas fa-arrow-left"></i>
        <span style={{ marginLeft: 10 }}>Go Back</span>
      </div>
      <div className="row">
        <div className="col-12">
          {props.currentMovie.poster_path == null ? (
            <img src={""} alt="card image" />
          ) : (
            <img
              src={"http://image.tmdb.org/t/p/w185" + props.image}
              alt="card image"
            />
          )}
        </div>
      </div>
      <div className="col-sm-12 m8">
        <div className="info.container">
          <p></p>
          <h3> {props.currentMovie.title}</h3>
          <hr size="500px" />
          <p>
            {" "}
            <b>Release Date : </b>{" "}
            {new Intl.DateTimeFormat("en-GB", {
              year: "numeric",
              month: "long",
              day: "2-digit",
            }).format(props.currentMovie.release_date)}
          </p>
          <p>
            {" "}
            <b>Vote count : </b> {props.currentMovie.vote_count}
          </p>
          <p>
            {" "}
            <b>Rating : </b> {props.currentMovie.vote_average}/10
          </p>

          <p>{props.currentMovie.overview}</p>
        </div>
      </div>
    </div>
  );
};

export default MovieDetails;
